mapper.insert:
INSERT INTO TB_FILE (
  FILE_ID
  , FILE_PATH
  , FILE_NM
  , FILE_XTNS
  , FILE_SIZE
  , SVC_CD
  , TBL_NM
  , DSTN_CLMN_VAL
) VALUES

(
  #{item.fileId}
  , #{item.filePath}
  , #{item.fileName}
  , #{item.fileExtension}
  , #{item.fileSize}
  , #{item.serviceCode}
  , #{item.tableName}
  , #{item.distinguishColumnValue}
)

mapper.delete:
DELETE FROM TB_FILE
WHERE FILE_ID IN

  #{fileId}

mapper.select:
SELECT FILE_ID , FILE_PATH , FILE_NM , FILE_XTNS , FILE_SIZE , SVC_CD , TBL_NM , DSTN_CLMN_VAL , PROC_PRGM_ID , RGST_PROCR_ID , RGST_PROC_DTM , UPDT_PROCR_ID , UPDT_PROC_DTM FROM TB_FILE
AND SVC_CD = #{serviceCode}
AND TBL_NM = #{tableName}
AND DSTN_CLMN_VAL = #{distinguishColumnValue}
